#!/bin/env sh
echo "$(echo $(pactl get-sink-mute @DEFAULT_SINK@ | sed 's/Mute: //g')$(pactl get-sink-volume @DEFAULT_SINK@ | cut -d/ -f2 | sed '1p;d' | sed 's/^[[:space:]]*//g' | sed 's/[[:space:]]*$//g') | sed 's/yes0%/󰸈 0%/g' | sed 's/yes/󰖁 /g' | sed -E 's/no([0123456789])%|no([0123][0123456789])%|no(40)%/󰕿 \1\2\3%/g' | sed -E 's/no(4[123456789])%|no([567][0123456789])%|no(80)%/󰖀 \1\2\3%/g' | sed -E 's/no([0123456789]*)%/󰕾 \1%/g' | sed -E 's/(󰕾)|(󰖀)|(󰖁)|(󰸈)/%{T3}\1\2\3\4%{T-} /g' | sed -E 's/(󰕿)/%{T4}\1%{T-} /g')" >> /home/jeremy/.config/polybar/volume_file
